bylocation.map:
function(doc) {
  if (!doc.tableData) { return }
  const loc = doc.tableData['Lieu(x)'] || '*'
  emit(loc.indexOf('Montréal') === -1 ? loc : 'Montréal', doc.title)
}

bytitle.map:
function(doc) {
  if (!doc.tableData) { return }
  emit(doc.title, doc.title)
  if (doc.redirects && doc.redirects.length) {
    doc.redirects.forEach(function (x) {
      emit(x.title, doc.title)
    })
  }
}
