bysize.map:
function(doc) {
  if (!doc.tableData) { return }
  if (!doc.length) {
    emit([false], doc.title);
  } else {
    emit([true, Math.ceil(doc.length / 1000)], doc.title);
  }
}

bylocation.map:
function(doc) {
  if (!doc.tableData) { return }
  const loc = doc.tableData['Lieu(x)'] || '*'
  emit(loc.indexOf('Montréal') === -1 ? loc : 'Montréal', doc.title)
}

bytitle.map:
function(doc) {
  if (!doc.tableData) { return }
  emit(doc.title, doc.title)
  if (doc.redirects && doc.redirects.length) {
    doc.redirects.forEach(function (x) {
      emit(x.title, doc.title)
    })
  }
}

bycategory.map:
function(doc) {
  if (!doc.tableData) { return }

  if (doc.categories && doc.categories.length) {
    doc.categories.forEach(function (x) { emit(x['*'], doc.title) })
  } else {
    emit('*', doc.title)
  }
}
